from typing import List, Optional

def find_first_duplicate(nums: List[int]) -> Optional[int]:
    if not isinstance(nums, list) or not all(isinstance(num, int) for num in nums):
        raise TypeError("Input must be a list of integers")

    seen = set()
    for num in nums:
        if num in seen:
            return num
        seen.add(num)

    return None

# Example usage:
arr = [2, 1, 3, 5, 3, 2]
print(find_first_duplicate(arr))  # Output: 3

from typing import List

def find_product(arr: List[int]) -> int:
    if not isinstance(arr, list) or not all(isinstance(x, int) for x in arr):
        raise TypeError("Input must be a list of integers")

    # Dictionary to store the count of each element
    element_count = {}

    # Count the occurrences of each element in the array
    for num in arr:
        if num in element_count:
            element_count[num] += 1
        else:
            element_count[num] = 1

    # Calculate the product of non-repeated elements
    product = 1
    has_non_repeated = False
    for num, count in element_count.items():
        if count == 1:
            product *= num
            has_non_repeated = True

    # If there are no non-repeated elements, return 1
    return product if has_non_repeated else 1

# Example usage:
arr = [2, 3, 4, 2, 5, 3, 6]
print(find_product(arr))  # Output: 120 (4 * 5 * 6)

from typing import List, Optional

def get_odd_occurrence(arr: List[int], arr_size: int) -> Optional[int]:
    if not isinstance(arr, list) or not all(isinstance(x, int) for x in arr):
        raise TypeError("Input must be a list of integers.")

    if arr_size == 0:
        return None

    # Create a dictionary to store the count of each element
    count_dict = {}

    # Count the occurrences of each element in the array
    for num in arr:
        if num in count_dict:
            count_dict[num] += 1
        else:
            count_dict[num] = 1

    # Find and return the element with an odd count
    for num, count in count_dict.items():
        if count % 2 != 0:
            return num

    # If no element is found, return None
    return None

# Example usage:
arr = [1, 2, 3, 2, 3, 1, 3]
result = get_odd_occurrence(arr, len(arr))
print(result)  # Output: 3

def count_substring_with_equal_ends(s: str) -> int:
    if not isinstance(s, str):
        raise TypeError("Input must be a string")

    if s == "":
        return 0

    # Dictionary to store the frequency of each character
    frequency = {}

    # Count the frequency of each character in the string
    for char in s:
        if char in frequency:
            frequency[char] += 1
        else:
            frequency[char] = 1

    # Calculate the number of substrings for each character
    count = 0
    for n in frequency.values():
        count += (n * (n + 1)) // 2

    return count

# Example usage:
s = "abcab"
print(count_substring_with_equal_ends(s))  # Output: 7

from typing import List, Union

def div_even_odd(numbers: List[int]) -> Union[int, float]:
    first_even = None
    first_odd = None

    for num in numbers:
        if first_even is None and num % 2 == 0:
            first_even = num
        if first_odd is None and num % 2 != 0:
            first_odd = num
        if first_even is not None and first_odd is not None:
            break

    if first_even is None or first_odd is None:
        raise ValueError("List must contain at least one even and one odd number.")

    return first_even / first_odd

# Example usage:
numbers = [5, 3, 8, 6, 7]
try:
    result = div_even_odd(numbers)
    print("Division of first even and odd number:", result)
except ValueError as e:
    print(e)

