def is_woodall(number):
    if not isinstance(number, int):
        raise TypeError("Input must be an integer")

    if number < 0:
        return False

    n = 1
    while True:
        woodall_number = n * (2 ** n) - 1
        if woodall_number == number:
            return True
        elif woodall_number > number:
            return False
        n += 1

# Example usage:
