def find_product(arr):
    # Check if the input is a list
    if not isinstance(arr, list):
        raise TypeError("Input must be a list")

    # Check if all elements in the list are integers
    if not all(isinstance(num, int) for num in arr):
        raise TypeError("All elements in the list must be integers")

    # Dictionary to store the count of each element
    element_count = {}

    # Count the occurrences of each element in the array
    for num in arr:
        if num in element_count:
            element_count[num] += 1
        else:
            element_count[num] = 1

    # Calculate the product of non-repeated elements
    product = 1
    has_non_repeated = False
    for num, count in element_count.items():
        if count == 1:
            product *= num
            has_non_repeated = True

    # If there are no non-repeated elements, return 0
    return product if has_non_repeated else 0

