def maximum_sum(list_of_lists):
    """
    Finds the maximum sum of elements among all sublists in a given list of lists.

    Parameters:
    - list_of_lists (list of lists): A list containing multiple sublists of integers.

    Raises:
    - TypeError: If the input is not a list of lists or if any sublist contains non-integer elements.

    Returns:
    - int: The maximum sum of elements found in any of the sublists.
    """
    if not isinstance(list_of_lists, list):
        raise TypeError("Input must be a list of lists.")

    max_sum = float('-inf')  # Initialize with negative infinity to handle negative numbers as well

    for sublist in list_of_lists:
        if not isinstance(sublist, list):
            raise TypeError("Each element of the input list must be a sublist.")

        for element in sublist:
            if not isinstance(element, int):
                raise TypeError("Each element of the sublists must be an integer.")

        current_sum = sum(sublist)
        if current_sum > max_sum:
            max_sum = current_sum

    return max_sum

# Example usage:
try:
    example_list_of_lists = [[1, 2, 3], [4, 5, 6], [-7, -8, 9], [0]]
    print(maximum_sum(example_list_of_lists))  # Output should be 15 (from the sublist [4, 5, 6])
except TypeError as e:
    print(e)

from collections import Counter

def find_product(arr):
    # Check if the input is a list
    if not isinstance(arr, list):
        raise TypeError("Input must be a list.")

    # Check if all elements in the list are integers
    if not all(isinstance(x, int) for x in arr):
        raise TypeError("All elements in the list must be integers.")

    # Count the frequency of each element in the array
    freq = Counter(arr)

    # Calculate the product of non-repeated elements
    product = 1
    for element, count in freq.items():
        if count == 1:
            product *= element

    return product

# Example usage:
