    print(find_product([2, 3, 4, 5, 6, 3, 4]))  # Output should be 2 * 5 * 6 = 60
    print(find_product([2, 3, 4, "5", 6, 3, 4]))  # Should raise TypeError
except TypeError as e:
    print(e)

def check_k_elements(tuples_list, k):
    """
    Checks if all tuples in the list have exactly k elements.

    :param tuples_list: A list of tuples to be checked.
    :param k: An integer representing the exact number of elements each tuple must have.
    :return: True if all tuples contain exactly k elements, False otherwise.
    """
    # Type checking
    if not isinstance(tuples_list, list):
        raise TypeError("tuples_list must be a list")
    if not all(isinstance(t, tuple) for t in tuples_list):
        raise TypeError("All elements in tuples_list must be tuples")
    if not isinstance(k, int):
        raise TypeError("k must be an integer")

    # Check if all tuples have exactly k elements
    return all(len(t) == k for t in tuples_list)

