    print(check_k_elements([(1, 2), (3, 4), (5, 6)], 2))  # Output: True
    print(check_k_elements([(1, 2), (3, 4, 5), (5, 6)], 2))  # Output: False
    print(check_k_elements([(1, 2), (3, 4)], '2'))  # Raises TypeError
except TypeError as e:
    print(e)

def get_odd_occurrence(arr):
    # Check if the input is a list
    if not isinstance(arr, list):
        raise TypeError("Input must be a list.")

    # Check if all elements in the list are integers
    if not all(isinstance(x, int) for x in arr):
        raise TypeError("All elements in the list must be integers.")

    # Dictionary to store the count of each element
    count_dict = {}

    # Count the occurrences of each element
    for element in arr:
        if element in count_dict:
            count_dict[element] += 1
        else:
            count_dict[element] = 1

    # Find and return the element with an odd occurrence
    for element, count in count_dict.items():
        if count % 2 != 0:
            return element

    # If no element occurs an odd number of times, return None
    return None

