def count_substring_with_equal_ends_brute_force(s):
    if not isinstance(s, str):
        raise TypeError("Input must be a string")

    count = 0
    n = len(s)

    for i in range(n):
        for length in range(1, n - i + 1):
            if s[i] == s[i + length - 1]:
                count += 1

    return count

