def last_non_zero_digit(n):
    if n < 5:
        # Direct computation for small n
        fact = 1
        for i in range(2, n + 1):
            fact *= i
        return str(fact)[-1]

    # For larger n, we remove factors of 10 and take modulo 10
    last_digit = 1
    for i in range(2, n + 1):
        while i % 5 == 0:
            i //= 5
        last_digit = (last_digit * (i % 10)) % 10

    # Adjust for powers of 2 left over
    power_of_two = 0
    for i in range(2, n + 1):
        while i % 2 == 0:
            i //= 2
            power_of_two += 1

    # Adjust based on the number of factors of 5 and 2
    if power_of_two > (n // 5 + n // 25 + n // 125 + n // 625):
        last_digit *= 6
    elif power_of_two > (n // 5 + n // 25 + n // 125):
        last_digit *= 4
    else:
        last_digit *= 2

    return last_digit % 10

def compute_last_digit(a, b):
    if a > b:
        raise ValueError('a should not be greater than b')

    last_digit_a = last_non_zero_digit(a)
    last_digit_b = last_non_zero_digit(b)

    # Calculate the last digit of the division
    if last_digit_b != 0:
        result = (last_digit_a * pow(last_digit_b, -1, 10)) % 10
    else:
        result = 0  # Division by zero case

    return result

