def specified_element(two_dim_list, index):
    """
    Extracts the element at the specified index from each sub-list of a two-dimensional list.

    :param two_dim_list: A two-dimensional list (list of lists)
    :param index: The index of the element to extract from each sub-list
    :return: A list containing the extracted elements
    """
    extracted_elements = []

    for sublist in two_dim_list:
        if 0 <= index < len(sublist):
            extracted_elements.append(sublist[index])
        else:
            raise IndexError(f'Index {index} is out of range for sublist {sublist}')

    return extracted_elements

