def min_length_list(lists):
    # Check if the input is a list
    if not isinstance(lists, list):
        raise ValueError('Input must be a list of lists.')

    # Check if every element in the list is also a list
    if not all(isinstance(lst, list) for lst in lists):
        raise ValueError('Input must be a list of lists.')

    # Use the min function with a key that returns the length of each list
    min_list = min(lists, key=lambda x: len(x))
    return (len(min_list), min_list)

