def nth_octagonal_number(n):
    if not isinstance(n, int) or n <= 0:
        raise ValueError("Input must be a positive integer.")
    return n * (3 * n - 2)

def max_len_sub(arr):
    # Check if the input is a list
    if not isinstance(arr, list):
        raise TypeError("The first argument must be a list.")

    # Check if the list is empty
    if not arr:
        return 0

    n = len(arr)
    max_length = 1  # At least one element can form a subsequence of length 1

    for i in range(n):
        current_length = 1
        for j in range(i + 1, n):
            if abs(arr[j] - arr[i]) == 1:
                current_length += 1
                i = j  # Move i to j to continue the sequence from the new end
            else:
                break  # If the condition fails, break and start checking from i+1
        max_length = max(max_length, current_length)

    return max_length

# Example usage:
try:
    print(max_len_sub([1, 2, 3, 4, 5, 3, 2, 1]))  # Expected output: 5
    print(max_len_sub([10]))                    # Expected output: 1
    print(max_len_sub('not a list'))            # Should raise TypeError
except TypeError as e:
    print(e)
