def test_duplicate(nums):
    if not isinstance(nums, list):
        raise TypeError("Input must be a list of integers")

    seen = set()
    for num in nums:
        if not isinstance(num, int):
            raise TypeError("All elements in the list must be integers")
        if num in seen:
            return True
        seen.add(num)
    return False

# Example usage:
try:
    nums = [1, 2, 3, 4, 5, 1]
    print(test_duplicate(nums))  # Output: True

    nums = [1, 2, 3, 4, 5]
    print(test_duplicate(nums))  # Output: False

    nums = "not a list"
    print(test_duplicate(nums))  # Should raise TypeError

    nums = [1, 2, 3, "4", 5]
    print(test_duplicate(nums))  # Should raise TypeError
except TypeError as e:
    print(e)

def div_even_odd(numbers):
    first_even = None
    first_odd = None

    # Iterate through the list to find the first even and odd numbers
    for num in numbers:
        if num % 2 == 0 and first_even is None:
            first_even = num
        elif num % 2 != 0 and first_odd is None:
            first_odd = num

        # Break the loop if both even and odd numbers are found
        if first_even is not None and first_odd is not None:
            break

    # Check if both even and odd numbers were found
    if first_even is None:
        raise ValueError("No even number found in the list.")
    if first_odd is None:
        raise ValueError("No odd number found in the list.")

    # Perform the division and return the result
    if first_odd == 0:
        raise ValueError("First odd number is zero, cannot divide by zero.")

    return float(first_even) / first_odd

# Example usage:
try:
    numbers_list = [7, 3, 4, 8, 9]
    result = div_even_odd(numbers_list)
    print(result)  # Output: 1.3333333333333333
except ValueError as e:
    print(e)
