def bytes_to_int(byte_string, byteorder='big', signed=True):
    # Calculate the bit width of the integer based on the byte string length
    bit_width = len(byte_string) * 8
    # Determine the maximum value based on the bit width and whether it's signed
    if signed:
        if bit_width == 64:
            max_value = 9223372036854775807
        elif bit_width == 32:
            max_value = 2147483647
        elif bit_width == 16:
            max_value = 32767
        elif bit_width == 8:
            max_value = 127
        else:
            raise ValueError("Unsupported bit width for a signed integer.")
    else:
        if bit_width == 64:
            max_value = 18446744073709551615
        elif bit_width == 32:
            max_value = 4294967295
        elif bit_width == 16:
            max_value = 65535
        elif bit_width == 8:
            max_value = 255
        else:
            raise ValueError("Unsupported bit width for an unsigned integer.")

    # Convert the byte string to an integer, ensuring it's within the correct range
    integer = int.from_bytes(byte_string, byteorder=byteorder, signed=signed)
    if signed and integer > max_value:
        integer -= 2 ** bit_width
    return integer

