import re

def render_template(string, context):
    # Define a pattern to match placeholders and their default values
    pattern = re.compile(r'\{(\w+)\}|(\{\w+\||\w+\})')

    # Function to replace a single placeholder
    def replace_placeholder(match):
        placeholder = match.group(1) or match.group(2).split('|')[0].strip('{}')
        default_value = match.group(2).split('|')[1] if '|' in match.group(2) else None
        value = context.get(placeholder, default_value)
        return str(value)

    # Replace placeholders in the string
    return pattern.sub(replace_placeholder, string)

