def bytes_to_int(byte_string, byteorder='big', signed=True):
    # Determine the bit width of the integer based on the byte length
    bit_width = len(byte_string) * 8
    if bit_width == 8:
        if signed:
            # Handle 8-bit signed integer
            if byte_string[0] & 0x80:
                # Negative value, sign extend
                return -((1 << bit_width) - int.from_bytes(byte_string, byteorder=byteorder))
            else:
                # Positive value
                return int.from_bytes(byte_string, byteorder=byteorder)
        else:
            # Handle 8-bit unsigned integer
            return int.from_bytes(byte_string, byteorder=byteorder)
    elif bit_width == 32:
        if signed:
            # Handle 32-bit signed integer
            if byte_string[0] & 0x80:
                # Negative value, sign extend
                return -((1 << bit_width) - int.from_bytes(byte_string, byteorder=byteorder))
            else:
                # Positive value
                return int.from_bytes(byte_string, byteorder=byteorder)
        else:
            # Handle 32-bit unsigned integer
            return int.from_bytes(byte_string, byteorder=byteorder)
    else:
        # Handle other bit widths
        return int.from_bytes(byte_string, byteorder=byteorder)

