import re

def render_template(string, context):
    # Define a pattern to match placeholders and their default values
    pattern = re.compile(r'\{(\w+)\}|(\{\w+\})')

    def replace_placeholder(match):
        # Extract the placeholder and default value from the match
        placeholder, default_value = match.groups()
        if placeholder:
            # If there is a placeholder without a default value, use the context value or default
            return context.get(placeholder, default_value or '')
        else:
            # If there is a placeholder with a default value, use the default value
            return default_value

    # Replace placeholders with their corresponding values or default values
    return pattern.sub(replace_placeholder, string)

