def decode(b):
    """
    Decodes IMAP modified UTF-7 encoded bytes back to a string.

    :param b: bytes
    :return: str
    """
    if not isinstance(b, bytes):
        return b

    decoded = []
    i = 0
    while i < len(b):
        byte = b[i]
        i += 1
        if byte == ord('<'):
            j = i
            while j < len(b) and b[j] != ord('>'):
                j += 1
            if j >= len(b):
                raise ValueError("Unterminated <...> sequence")
            hex_part = b[i:j].decode('ascii').replace('=', '')
            if len(hex_part) % 2 != 0:
                raise ValueError("Invalid hexadecimal sequence length")
            unicode_code_point = int(hex_part, 16)
            decoded.append(chr(unicode_code_point))
            i = j + 1
        elif byte == ord('-') or byte == ord(',') or byte == ord('>'):
            decoded.append(byte.decode('ascii'))
        else:
            decoded.append(chr(-byte))

    return ''.join(decoded)

